import React, { useState, useEffect, useRef } from 'react';
import ReactPlayer from 'react-player';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext';
import toast from 'react-hot-toast';

const Room = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const { socket, isConnected, joinRoom, leaveRoom, sendMessage: sendSocketMessage, onEvent, sendVideoControl } = useSocket();
  
  const [room, setRoom] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [usersInRoom, setUsersInRoom] = useState([]);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const [isAudioEnabled, setIsAudioEnabled] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  
  const videoRef = useRef(null);
  const playerRef = useRef(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (!socket || !isConnected) return;

    joinRoom(roomId);

    const offChat = onEvent('chat-message', (data) => {
      setMessages(prev => [...prev, {
        userId: data.userId,
        username: data.username,
        content: data.message,
        timestamp: data.timestamp
      }]);
    });

    const offJoined = onEvent('user-joined', (userData) => {
      toast.success(`${userData.username} ƒë√£ tham gia ph√≤ng`);
    });

    const offUsers = onEvent('room-users', (users) => {
      setUsersInRoom(users);
    });

    const offVideo = onEvent('video-control', (data) => {
      const { action, time } = data;
      const isYouTube = (room?.videoType || '').toLowerCase() === 'youtube' || (room?.videoUrl || '').includes('youtu');

      if (isYouTube) {
        const player = playerRef.current;
        if (!player) return;
        if (typeof time === 'number' && !Number.isNaN(time)) {
          try { player.seekTo(time, 'seconds'); } catch (_) {}
        }
        if (action === 'play') {
          setIsPlaying(true);
        } else if (action === 'pause') {
          setIsPlaying(false);
        }
      } else {
        const video = videoRef.current;
        if (!video) return;
        if (typeof time === 'number' && !Number.isNaN(time)) {
          const drift = Math.abs(video.currentTime - time);
          if (drift > 0.3) {
            video.currentTime = time;
          }
        }
        if (action === 'play') {
          video.play().catch(() => {});
        } else if (action === 'pause') {
          video.pause();
        } else if (action === 'seek') {
          if (typeof time === 'number') {
            video.currentTime = time;
          }
        }
      }
    });

    // L·∫•y th√¥ng tin ph√≤ng
    (async () => {
      try {
        const response = await fetch(`/api/rooms/${roomId}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        if (response.ok) {
          const roomData = await response.json();
          setRoom(roomData.room || roomData);
        } else {
          toast.error('Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ph√≤ng');
          navigate('/dashboard');
        }
      } catch (error) {
        console.error('L·ªói khi t·∫£i th√¥ng tin ph√≤ng:', error);
        toast.error('L·ªói k·∫øt n·ªëi');
      }
    })();

    return () => {
      leaveRoom(roomId);
      offChat && offChat();
      offJoined && offJoined();
      offUsers && offUsers();
      offVideo && offVideo();
    };
  }, [socket, isConnected, roomId, user, joinRoom, leaveRoom, onEvent, navigate]);
  

  const sendMessage = () => {
    if (!newMessage.trim()) return;

    sendSocketMessage(roomId, newMessage);
    setNewMessage('');
  };

  const toggleVideo = () => {
    setIsVideoEnabled(!isVideoEnabled);
    // Implement video toggle logic here
  };

  const toggleAudio = () => {
    setIsAudioEnabled(!isAudioEnabled);
    // Implement audio toggle logic here
  };

  const handleLeaveRoom = () => {
    leaveRoom(roomId);
    navigate('/dashboard');
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(scrollToBottom, [messages]);

  // G·ª≠i s·ª± ki·ªán ƒëi·ªÅu khi·ªÉn video khi local video thay ƒë·ªïi (HTML5 video)
  useEffect(() => {
    const video = videoRef.current;
    if (!video || !socket || !isConnected) return;

    const handlePlay = () => sendVideoControl(roomId, 'play', video.currentTime);
    const handlePause = () => sendVideoControl(roomId, 'pause', video.currentTime);
    const handleSeeked = () => sendVideoControl(roomId, 'seek', video.currentTime);

    video.addEventListener('play', handlePlay);
    video.addEventListener('pause', handlePause);
    video.addEventListener('seeked', handleSeeked);

    return () => {
      video.removeEventListener('play', handlePlay);
      video.removeEventListener('pause', handlePause);
      video.removeEventListener('seeked', handleSeeked);
    };
  }, [socket, isConnected, roomId, sendVideoControl]);

  const handleReactPlayerPlay = () => {
    const player = playerRef.current;
    const current = player?.getCurrentTime ? player.getCurrentTime() : 0;
    setIsPlaying(true);
    sendVideoControl(roomId, 'play', current || 0);
  };

  const handleReactPlayerPause = () => {
    const player = playerRef.current;
    const current = player?.getCurrentTime ? player.getCurrentTime() : 0;
    setIsPlaying(false);
    sendVideoControl(roomId, 'pause', current || 0);
  };

  const handleReactPlayerSeek = (seconds) => {
    sendVideoControl(roomId, 'seek', seconds || 0);
  };

  if (!room) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">{room.name}</h1>
              <p className="text-gray-600">{usersInRoom.length} ng∆∞·ªùi tham gia</p>
            </div>
            <div className="flex space-x-3">
              <button
                onClick={toggleVideo}
                className={`px-4 py-2 rounded-lg ${
                  isVideoEnabled 
                    ? 'bg-red-500 text-white' 
                    : 'bg-gray-200 text-gray-700'
                }`}
              >
                {isVideoEnabled ? 'T·∫Øt Video' : 'B·∫≠t Video'}
              </button>
              <button
                onClick={toggleAudio}
                className={`px-4 py-2 rounded-lg ${
                  isAudioEnabled 
                    ? 'bg-red-500 text-white' 
                    : 'bg-gray-200 text-gray-700'
                }`}
              >
                {isAudioEnabled ? 'T·∫Øt √Çm thanh' : 'B·∫≠t √Çm thanh'}
              </button>
              <button
                onClick={handleLeaveRoom}
                className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
              >
                R·ªùi ph√≤ng
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Video Area */}
          <div className="lg:col-span-2">
            <div className="bg-black rounded-lg aspect-video flex items-center justify-center">
              {isVideoEnabled ? (
                (() => {
                  const isYouTube = (room?.videoType || '').toLowerCase() === 'youtube' || (room?.videoUrl || '').includes('youtu');
                  if (isYouTube) {
                    return (
                      <ReactPlayer
                        ref={playerRef}
                        url={room?.videoUrl}
                        width="100%"
                        height="100%"
                        playing={isPlaying}
                        controls
                        onPlay={handleReactPlayerPlay}
                        onPause={handleReactPlayerPause}
                        onSeek={handleReactPlayerSeek}
                      />
                    );
                  }
                  return (
                    <video
                      ref={videoRef}
                      className="w-full h-full object-cover rounded-lg"
                      autoPlay
                      muted={!isAudioEnabled}
                      controls
                      src={room?.videoUrl || ''}
                    />
                  );
                })()
              ) : (
                <div className="text-white text-center">
                  <div className="text-6xl mb-4">üìπ</div>
                  <p>Video ƒë√£ t·∫Øt</p>
                </div>
              )}
            </div>
            
            {/* Video Controls */}
            <div className="mt-4 flex justify-center space-x-4">
              <button
                onClick={toggleVideo}
                className={`p-3 rounded-full ${
                  isVideoEnabled ? 'bg-red-500 text-white' : 'bg-gray-300 text-gray-700'
                }`}
              >
                {isVideoEnabled ? 'üìπ' : 'üö´'}
              </button>
              <button
                onClick={toggleAudio}
                className={`p-3 rounded-full ${
                  isAudioEnabled ? 'bg-red-500 text-white' : 'bg-gray-300 text-gray-700'
                }`}
              >
                {isAudioEnabled ? 'üîä' : 'üîá'}
              </button>
            </div>
          </div>

          {/* Chat Area */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-sm border h-96 flex flex-col">
              {/* Chat Header */}
              <div className="px-4 py-3 border-b bg-gray-50">
                <h3 className="font-semibold text-gray-900">Chat</h3>
              </div>

              {/* Messages */}
              <div className="flex-1 overflow-y-auto p-4 space-y-3">
                {messages.map((message, index) => (
                  <div
                    key={index}
                    className={`flex ${
                      message.userId === user._id ? 'justify-end' : 'justify-start'
                    }`}
                  >
                    <div
                      className={`max-w-xs px-3 py-2 rounded-lg ${
                        message.userId === user._id
                          ? 'bg-blue-500 text-white'
                          : 'bg-gray-200 text-gray-800'
                      }`}
                    >
                      <div className="text-xs opacity-75 mb-1">
                        {message.username}
                      </div>
                      <div>{message.content}</div>
                    </div>
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>

              {/* Message Input */}
              <div className="p-4 border-t">
                <div className="flex space-x-2">
                  <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                    placeholder="Nh·∫≠p tin nh·∫Øn..."
                    className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                  <button
                    onClick={sendMessage}
                    className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                  >
                    G·ª≠i
                  </button>
                </div>
              </div>
            </div>

            {/* Users in Room */}
            <div className="mt-4 bg-white rounded-lg shadow-sm border">
              <div className="px-4 py-3 border-b bg-gray-50">
                <h3 className="font-semibold text-gray-900">Ng∆∞·ªùi tham gia</h3>
              </div>
              <div className="p-4">
                <div className="space-y-2">
                  {usersInRoom.map((userId) => (
                    <div key={userId} className="flex items-center space-x-2">
                      <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                      <span className="text-sm text-gray-700">
                        {userId === user._id ? 'B·∫°n' : `User ${userId.slice(-4)}`}
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Room;
